import React from 'react';

class Statistics extends React.Component {

  constructor () {
    super ()
    this.state = {
      countries: {}
    }
  }

  componentDidMount() {
      let list={}
      fetch ('catalog/countries')
      .then (res => res.json())
      .then((count) => {count.forEach ((one) => list[one]=0); this.setState({countries: list})
      })

    }



        render () {
        let topcountries=this.state.countries

        this.props.stat.forEach(data=> {data.viscountries.forEach(country => topcountries[country]+=1)})

        topcountries.sort((a, b) => a > b ? 1 : -1);
        console.log(topcountries)

            return (
                <div>
                    <h3>Top of the most popular countries for travel:</h3>
                    {this.state.countries.map((country, ind) =>
                <option key={ind} value = {country}>{country}</option>)}
                </div>

            )
        }
}


export default Statistics
