const initialState= {
    in: 0
}
function signInOutReducer (state=initialState, action) {
    switch (action.type) {
        case 'SIGN_IN': 
        return [
            ...state,
            {
                in:  action.result,
                username: action.username,
                country: action.country
            }
          ]
       /* return Object.assign({}, {
            in:  action.result,
            username: action.username,
            country: action.country
        })*/
        case 'SIGN_OUT': {
            return Object.assign({}, {
                in: action.result,
                username: null,
                county: null
            })
        }
        default:
            return state
    }
}

export default signInOutReducer
