import store from '../store'
const initialState= {
    usercountry: store.getState().in.country
}

function changeStatReducer (state=initialState, action) {
    switch (action.type) {
        case 'CHANGE_STAT': 
        return  [
            ...state, {
            viscountries: action.countries
            } 
        ]

        default:
            return state
    }
}

export default changeStatReducer
