import axios from 'axios'
export default function LoadStatAction (){
    let statistics=[]
    axios.get ('/catalog/statistics')
        .then (res =>statistics.push(res.data))
    return {
        type: 'LOAD_STAT', 
        stat:statistics
        }
}
