import React from 'react';
import store from '../store'

class Profile extends React.Component {
    constructor (props) {
    super (props)
    this.state = { countries: []}
    this.Finder=this.Finder.bind(this)};


    Finder(name) {
        const allstat=store.getState().statistics
        return allstat.find(info => info.username === name).viscountries 
    }

    componentDidMount() {
        const viscountries=Finder (store.getState().in.username)
        this.setState ({countries : viscountries})
    }

    render () {
            return (
                <div>
                    <h3>My travels</h3>
                    {this.state.countries.map((country, ind) =>
                    <p key={ind}>{ind+1} 
                    <NavLink to={String('/countries/'+ country)}> {country}</NavLink>
                    </p>
                    )}
                </div>
            )
        }
}


export default Profile
