const initialState= []

function changeStatReducer (state=initialState, action) {
    switch (action.type) {
        case 'CHANGE_STAT':
        return  [
            ...state, 
            {username: action.username, usercountry: action.usercountry,
            viscountries: action.viscountries}
            ]

        case 'DELETE':
        const newstat=[]
            state.forEach(info => {if (info.username !== action.username) {
                    newstat.push (info)}})
        return newstat

        case 'CHANGE_NAME': 
        const newstatis=[]
         state.forEach(info => {if (info.username !== action.oldname) {
            newstatis.push ({username: action.newname, viscountries: info.viscountries})
        }
        else { newstatis.push (info)}}
        )
        return newstatis

        default:
            return state
    }
}

export default changeStatReducer
