import axios from 'axios'
function LoadStatAction (bool){
    console.log('loading action')
    return  {
                type: 'STATISTICS_IS_LOADING',
                isLoading: bool
            }; 
}

function StatHasLoaded (statistics) {
    return  {
        type: 'STATISTICS_HAS_LOADED',
        statistics
    }; 
}

export function FetchingStat() {
    console.log('in fetching')
    return (dispatch) => {
        console.log('inside')
        axios.get('/catalog/statistics')
            .then((response) => {
                return response;
            })
            .then((response) => response.json())
            .then((items) => {console.log(items);dispatch(StatHasLoaded(items))})
    };
}
