import React from 'react'/*
import SignInRequest from "../signInRequest";*/
import SignInForm from "./SignInForm";
import {connect} from 'react-redux'
import axios from 'axios'
function SignInRequest(userData) {
    console.log('sugninrequest')
    return axios.get ('catalog/signin', {params: {username: userData.username, password : userData.password}})
}

const SignIn = () => {
    return (
    <SignInForm onSubmit={SignInRequest} />
)}

export default connect (null, {SignInRequest})(SignIn);
