export  function LoadStatAction (bool){

    return  {
                type: 'STATISTICS_IS_LOADING',
                isLoading: bool
            }; 
}

export function StatHasLoaded (statistics) {
    return  {
        type: 'STATISTICS_HAS_LOADED',
        statistics
    }; 
}

export function FetchingStatAction() {
    return (dispatch) => {
        dispatch(LoadStatAction(true));
        fetch('/catalog/statistics')
            .then((response) => {

                dispatch(LoadStatAction(false));
                return response;
            })
            .then((response) => response.json())
            .then((items) => dispatch(StatHasLoaded(items)))
    };
}
