  return  b.visits - a.visits;
}

class Statistics extends React.Component {

  constructor () {
    super ()
    this.state = {
      countries: []
    }
  }

  componentDidMount() {
      let list=[]
      fetch ('catalog/countries')
      .then (res => res.json())
      .then((count) => {count.forEach ((one) => list.push ({'name': one, 'visits': 0, 'fromwhere': null}));this.setState({countries: list})})
    }



