import axios from 'axios'
export  function LoadStatAction (bool){
    console.log('loading action')
    return  {
                type: 'STATISTICS_IS_LOADING',
                isLoading: bool
            }; 
}

export function StatHasLoaded (statistics) {
    return  {
        type: 'STATISTICS_HAS_LOADED',
        statistics
    }; 
}

export function FetchingStat() {
    console.log('in fetching')
    return (dispatch) => {
        console.log('inside')
        dispatch(LoadStatAction(true));
        axios.get(`https://www.reddit.com/r/${subreddit}.json`)
            .then((response) => {
                dispatch(LoadStatAction(false));
                return response;
            })
            .then((response) => response.json())
            .then((items) => {console.log(items);dispatch(StatHasLoaded(items))})
    };
  }
