import MyTravelsForm from './MyTravelsForm';
import React from 'react'
import {connect} from 'react-redux'
import store from '../store';
import changeStatAction from '../actions/changeStatAction';

const SendingStatistics = (countries) => {
        const usercountry=store.getState().in.country
        const username=store.getState().in.username
        const viscountries=Object.keys(countries)
        store.dispatch (changeStatAction(username, usercountry, viscountries))
        console.log('send!')
}


const MyTravels = () => {
    if (store.getState().in.in){
    return (
    <MyTravelsForm onSubmit={SendingStatistics} />)}
    else {
            return (
                <h3>Firstly, you need to sign in</h3>
            )

    }
}

export default connect (null, {SendingStatistics})(MyTravels);
