const initialState= {
    in: 0
}
function signInOutReducer (state=initialState, action) {
    switch (action.type) {
        case 'SIGN_IN': 
        return Object.assign({}, state, {
            in:  action.result,
            username: action.username,
            country: action.country
        })
        case 'SIGN_OUT': {
            return Object.assign({}, state, {
                in: action.result
            })
        }
        default:
            return state
    }
}

export default signInOutReducer
