import axios from 'axios'
export default function LoadStatAction (){
    let statistics=[]
    axios.get ('/catalog/statistics')
        .then (res =>statistics.push(res.data))
    if (statistics===undefined) {
        statistics=null
    }

    return {
        type: 'LOAD_STAT', 
        stat:statistics[0]
        }
}
