import React from 'react';
import store from '../store'
import {NavLink} from 'react-router-dom'

class Profile extends React.Component {
    constructor (props) {
    super (props)
    this.state = { countries: []}
   };

    componentDidMount() {
        const allstat=this.props.stat
        if (!this.props.stat) {
        const viscountr= allstat.find(info => info.username === this.props.username).viscountries  
        this.setState ({countries : viscountr})}
    }

    render () {
        if (this.props.in) {
            return (
                <div>
                    <h3>My profile</h3>
                    <p>Name: {this.props.username} </p>
                    <p>My travels</p>
                    {this.state.countries.map((country, ind) =>
                    <p key={ind}>{ind+1} 
                    <NavLink to={String('/countries/'+ country)}> {country}</NavLink>
                    </p>
                    )}
                </div>
            )}
        else {
            return (<h3>Firstly, you need to sign in</h3>)
        }
        }
}


export default Profile
