const initialState= []

function changeStatReducer (state=initialState, action) {
    switch (action.type) {
        case 'CHANGE_STAT':
        return  [
            ...state, 
            {username: action.username, usercountry: action.usercountry,
            viscountries: action.viscountries}
            ]

        case 'DELETE':
        const newstat=[]
            state.forEach(info => {if (info.username !== action.username) {
                    newstat.push (info)}})
        return newstat

        default:
            return state
    }
}

export default changeStatReducer
