import React from 'react';
import store from '../store'
import {NavLink} from 'react-router-dom'
import axios from 'axios';
import SignOutAction from '../actions/signOutAction';
import DeleteAction from '../actions/deleteAction';
import '../css/Button.css';

const ChangingName = () => {

}
const Deletting = () => {
    const username = store.getState().in.username
    axios.delete ('/catalog/signup', {params: {'username': username}})
        .then (store.dispatch (SignOutAction()))
        .then (store.dispatch(DeleteAction(username)))
}

class Profile extends React.Component {
    constructor (props) {
    super (props)
    this.state = { countries: []}
   };

    componentDidMount() {
        const allstat=this.props.stat
        console.log(allstate)
        if (allstat !==[]) {
        const viscountr= allstat.find(info => info.username === this.props.username).viscountries  
        this.setState ({countries : viscountr})}
    }

    render () {
        if (this.props.in) {
            return (
                <div class = 'col-9'>
                    <h3>My profile</h3>
                    <button class = 'nav-button' onClick={Deletting}>Delete profile </button>
                    <button class = 'nav-button' onClick={ChangingName}>Change name </button>  
                    <p>Name: <NavLink to='/changename'> {this.props.username}</NavLink> </p>
                    <p>My travels: </p>
                    {this.state.countries.map((country, ind) =>
                    <p key={ind}>{ind+1} 
                    <NavLink to={String('/countries/'+ country)}> {country}</NavLink>
                    </p>
                    )}
                </div>
            )}
        else {
            return (<h3>Firstly, you need to sign in</h3>)
        }
        }
}


export default Profile
