import React from 'react';
import './css/App.css';
import './css/Countries.css'
import { NavLink } from 'react-router-dom'


class Country extends React.Component {
   state = {
            name: null,
            area: null,
            region: null,
            population: null,
            neighbours: null,
            capital: null,
            currency: null}

    componentDidMount() {
      let address=`/catalog/countries/`+this.props.match.params.name
      console.log(address)
      fetch(address)
        .then (res => res.json())
        .then((country) => {
            console.log(country)
            this.setState((state) => {
                return {name: country.name, area: country.area, region: country.region, 
                        population: country.population, neighbours: country.neighbours, 
                        capital: country.capital, currency: country.currency}
            })
        });
   }

    render () {
    return (
    <div className = 'col-10'>
     <h1>{this.state.name}</h1>
     <p>Area: {this.state.area}</p>
     <p>Region: {this.state.region}</p>
     <p>Population: {this.state.population}</p>
     <p>Neighbours: {this.state.neighbours}</p>
     <p>Capital: {this.state.capital}</p>
     <p>Currency: {this.state.currency}</p>

     </div>
     );}
  }

  export default Country;
