import React from 'react';
import store from '../store'
import {NavLink} from 'react-router-dom'

class Profile extends React.Component {
    constructor (props) {
    super (props)
    this.state = { countries: []}
   };


    componentDidMount() {
        const allstat=store.getState().statistics
        const viscountr= allstat.find(info => info.username === store.getState().in.username).viscountries  
        this.setState ({countries : viscountr})
    }

    render () {
            return (
                <div>
                    <h3>My travels</h3>
                    {this.state.countries.map((country, ind) =>
                    <p key={ind}>{ind+1} 
                    <NavLink to={String('/countries/'+ country)}> {country}</NavLink>
                    </p>
                    )}
                </div>
            )
        }
}


export default Profile
