import React from 'react';

class Statistics extends React.Component {

  constructor () {
    super ()
    this.state = {
      countries: []
    }
  }

  componentDidMount() {
      let list=[]
      fetch ('catalog/countries')
      .then (res => res.json())
      .then((count) => {count.forEach ((one) => list.push ({'name': one, 'visits': 0}));this.setState({countries: list})})
    }



        render () {
        let topcountries=this.state.countries
        this.props.stat.forEach(data=> {data.viscountries.forEach(country => {
          if (topcountries.find (item => item.name==country)){
          topcountries.find (item => item.name==country).visits+=1}
        })})
        topcountries=topcuntries.slice(0, 10)

        function comparePop(a, b) {
          return  b.visits - a.visits;
        }
        topcountries.sort(comparePop);

        console.log(topcountries)

            return (
                <div>
                    <h3>Top 10 of the most popular countries for travel:</h3>
                    {topcountries.map (one => <p>{one.name} (Visits:{one.visits})</p>)}
                </div>

            )
        }
}


export default Statistics
