const Changing = (newname) => {

    const usercountry=store.getState().in.country
    const username=store.getState().in.username
    const viscountries=Object.keys(countries)
    store.dispatch (changeStatAction(username, usercountry, viscountries))
}


class ChangeName extends React.Component{
        constructor (props) {
        super (props) 
        this.state = {countries: []}
    }
        render () {
        const { handleSubmit, pristine, reset, submitting } = this.props
        if (this.props.in) {
        return (
           <form onSubmit={handleSubmit(Changing)}>
                <div>

                        <label> Enter new name </label>
                        <Field name='newname' component="input" type="texy" />

                </div>
                <button type = 'submit' disabled={submitting}>Change name</button>
                </form>
        )}
        else {
            return <h3>Firstly, you need to sign in</h3>
        }

}}

export default reduxForm ({
    form: 'ChangeNameForm'
})(ChangeName)
