import axios from 'axios'
export default function LoadStatAction (){
    let statistics=[]
    axios.get ('/catalog/statistics')
        .then (res =>res.json())
        .then (info => info.forEach (statistics.push(info)))

    return {
        type: 'LOAD_STAT', 
        stat:statistics
        }
}
