const initialState= {
    in: 0
}
function signInOutReducer (state=initialState, action) {
    switch (action.type) {
        case 'SIGN_IN': 
        return  {
            in:  action.result,
            username: action.username,
            country: action.country
        }
        case 'SIGN_OUT': {
            return {
                in: action.result,
                username: null,
                county: null
            }
        }
        default:
            return state
    }
}

export default signInOutReducer
