import store from '../store'

const initialState= {}

function changeStatReducer (state=initialState, action) {
    console.log('reducer')
    console.log(state.statistics)
    switch (action.type) {
        case 'CHANGE_STAT':
        return  [
            ...state.statistics, 
            {usercountry: action.usercountry,
            viscountries: action.viscountries}
            ]


        default:
            return state
    }
}

export default changeStatReducer
